#include "torus.h"

/*
 * Constructors and De-constructors
 */
torus::torus(){
}

torus::torus(	double inner_radius, double outer_radius, double nsides, double rings, double red, double green, double blue,
				double x_o, double y_o, double z_o ){
	this->inner_radius = inner_radius;
	this->outer_radius = outer_radius;
	this->nsides = nsides;
	this->rings = rings;
	if(z_o == 0 && x_o > 0){
		this->radial_distance = x_o;
		this->angular_position = 0;
	}else if(z_o != 0 && z_o > 0){
		this->radial_distance = z_o;
		this->angular_position = 90;
	}else if(z_o == 0 && x_o < 0){
		this->radial_distance = x_o;
		this->angular_position = 180;
	}else if(z_o != 0 && z_o < 0){
		this->radial_distance = z_o;
		this->angular_position = 270;
	}
	this->color[0] = red;
	this->color[1] = green;
	this->color[2] = blue;
	this->color[3] = 1.0;

	this->Matrix[0] = 1.0;
	this->Matrix[1] = 0.0;
	this->Matrix[2] = 0.0;
	this->Matrix[3] = 0.0;

	this->Matrix[4] = 0.0;
	this->Matrix[5] = 1.0;
	this->Matrix[6] = 0.0;
	this->Matrix[7] = 0.0;

	this->Matrix[8] = 0.0; 
	this->Matrix[9] = 0.0;  
	this->Matrix[10] = 1.0;
	this->Matrix[11] = 0.0;

	this->Matrix[12] = x_o;
	this->Matrix[13] = y_o;
	this->Matrix[14] = z_o;
	this->Matrix[15] = 1.0;

	this->rotateMatrix[0] = 1.0;
	this->rotateMatrix[1] = 0.0;
	this->rotateMatrix[2] = 0.0;
	this->rotateMatrix[3] = 0.0;

	this->rotateMatrix[4] = 0.0;
	this->rotateMatrix[5] = 1.0;
	this->rotateMatrix[6] = 0.0;
	this->rotateMatrix[7] = 0.0;

	this->rotateMatrix[8] = 0.0;
	this->rotateMatrix[9] = 0.0;
	this->rotateMatrix[10] = 1.0;
	this->rotateMatrix[11] = 0.0;

	this->rotateMatrix[12] = x_o;
	this->rotateMatrix[13] = y_o;
	this->rotateMatrix[14] = z_o;
	this->rotateMatrix[15] = 1.0;
}

torus::~torus(){
}

