void torus::rotate(GLfloat rotate){
	this->angular_position += rotate;
	if(this->angular_position > 360){
		this->angular_position = this->angular_position - 360;
	}
	//CALCULATE TORUS NEW LOCATION STORE INTO PLACE THAT WON'T EFFECT THE TRANSLATION
	this->Matrix[12] = this->radial_distance * cos(this->angular_position);
	this->Matrix[14] = this->radial_distance * sin(this->angular_position);
}

void torus::Display_Matrix(char* operation, GLfloat matrix[16]){
	printf("%s\n", operation);
	printf("%10f ", matrix[0]);
	printf("%10f ", matrix[4]);
	printf("%10f ", matrix[8]);
	printf("%10f ", matrix[12]);
	printf("\n");

	printf("%10f ", matrix[1]);
	printf("%10f ", matrix[5]);
	printf("%10f ", matrix[9]);
	printf("%10f ", matrix[13]);
	printf("\n");

	printf("%10f ", matrix[2]);
	printf("%10f ", matrix[6]);
	printf("%10f ", matrix[10]);
	printf("%10f ", matrix[14]);
	printf("\n");

	printf("%10f ", matrix[3]);
	printf("%10f ", matrix[7]);
	printf("%10f ", matrix[11]);
	printf("%10f ", matrix[15]);
	printf("\n"); 
}

